import com.eviware.soapui.support.GroovyUtils
import groovy.util.XmlSlurper
import jxl.*
//Create a file instance
groovyutils = new com.eviware.soapui.support.GroovyUtils(context)
projectpath = groovyutils.projectPath

log.info ("projectpath="+ projectpath)
def excelFilePath = projectpath+"\\testdata1.xls"

File excelFile = new File(excelFilePath)
//Create workbook instace
jxl.Workbook workbook = jxl.Workbook.getWorkbook(excelFile)
//Load the sheet
jxl.Sheet sheet = workbook.getSheet(0)
//I want to read the data in the sheet
int noOfRows = sheet.getRows()-1 //number of rows in the excel
log.info "noOfRows = $noOfRows"
//need to loop through the records and print data
def number1,number2
for(int rowIdx in 1..noOfRows){
	//read the contents of the cell.
	log.warn '-----------------------------------------'
	number1 = sheet.getCell(0,rowIdx).getContents() 
	number2 = sheet.getCell(1,rowIdx).getContents()
	log.info "Setting test case property Values  intA=$number1 ,intB=$number2"
	testRunner.testCase.setPropertyValue("intA",number1)
	testRunner.testCase.setPropertyValue("intB",number2)

	//execute the teststeps from this script dynamically for each set of data.
	log.info "Executing Add request .....intA=$number1 ,intB=$number2.......$number1 + $number2 "
	//testRunner.testCase.getTestStepByName('Add').run(testRunner.context);
	def addResponse=testRunner.testCase.testSuite.getTestCaseByName("Calculate").getTestStepByName("Add").run(testRunner, context)
	def xmlSlurper= new XmlSlurper()
	def xmlAddResponse=xmlSlurper.parseText(addResponse.responseContentAsXml)
	log.info(xmlAddResponse)

	log.info ("-----------------------------------------")
	log.info "Executing Divide request .....intA=$number1 ,intB=$number2.......$number1 / $number2..."
	def divideResponse=testRunner.testCase.testSuite.getTestCaseByName("Calculate").getTestStepByName("Divide").run(testRunner, context)
	def xmlDivResponse=xmlSlurper.parseText(divideResponse.responseContentAsXml)
	log.info(xmlDivResponse)
	log.info ("-----------------------------------------")
	log.info "Executing Multiply request .....intA=$number1 ,intB=$number2.........$number1 * $number2...."
	def multiplyResponse=testRunner.testCase.testSuite.getTestCaseByName("Calculate").getTestStepByName("Multiply").run(testRunner, context)
	def xmlMulResponse=xmlSlurper.parseText(multiplyResponse.responseContentAsXml)
	log.info(xmlMulResponse)
	log.info ("-----------------------------------------")
	log.info "Executing Subtract request .......intA=$number1 ,intB=$number2........$number1 - $number2...."
	def subtractResponse=testRunner.testCase.testSuite.getTestCaseByName("Calculate").getTestStepByName("Subtract").run(testRunner, context)
	def xmlSubResponse=xmlSlurper.parseText(subtractResponse.responseContentAsXml)
	log.info(xmlSubResponse)
log.info ("-----------------------------------------")
}
//close excel
workbook.close()

