import com.eviware.soapui.support.GroovyUtils
import com.eviware.soapui.support.XmlHolder
import groovy.util.XmlSlurper
import javax.xml.XMLConstants
import javax.xml.transform.stream.StreamSource
import javax.xml.validation.SchemaFactory

//assert endpoint
def endPoint="http://www.dneonline.com/calculator.asmx"
assert messageExchange.getEndpoint() == endPoint
log.info "End point :"+endPoint
//---------------------assert response code
def status = messageExchange.responseHeaders["#status#"]
assert status.toString().equals('[HTTP/1.1 200 OK]')
log.info "Responsecode :"+status

//---------------------assert response time
assert messageExchange.timeTaken<3000
log.info "TimeTaken for Message :" + messageExchange.timeTaken



//-----------------------retrieve request input and response result
def request=messageExchange.requestContentAsXml;
def response=messageExchange.responseContentAsXml;
def slurper = new XmlSlurper();
//def reqData=slurper.parseText (request)
def reqData=slurper.parseText (request)
//def xmldata = slurper.parseText (response);
def resData=slurper.parseText (response)

String number1=reqData.Body.Add.intA
log.info("intA = "+number1)
String  number2=reqData.Body.Add.intB
log.info("intB = "+number2)
def addition=Integer.parseInt(number1)+ Integer.parseInt(number2)
String addResult=resData.Body.AddResponse.AddResult
def additionResult=Integer.parseInt(addResult)
assert addition.equals(additionResult)
//assert addition.equals(addResult)
log.info (additionResult)

import com.eviware.soapui.support.XmlHolder
import javax.xml.XMLConstants
import javax.xml.transform.stream.StreamSource
import javax.xml.validation.SchemaFactory
import com.eviware.soapui.support.GroovyUtils

groovyutils = new com.eviware.soapui.support.GroovyUtils(context)
projectpath = groovyutils.projectPath

log.info ("projectpath="+ projectpath)
assert messageExchange.responseStatusCode == 200

def xsdFilePath = projectpath+"\\calculator.xsd"

String response = messageExchange.responseContent
log.info ("response="+response);

// Create validation objects
def factory = SchemaFactory.newInstance(XMLConstants.W3C_XML_SCHEMA_NS_URI);
def schema = factory.newSchema(new StreamSource(xsdFilePath));
def validator = schema.newValidator();

// Validate the response against the schema
assert validator.validate(new StreamSource(new StringReader(response))) == null;

//validate content
def holder = new XmlHolder( messageExchange.responseContentAsXml )
//log.info holder.;
String result = holder.getNodeValue( "//AddResult" )
log.info ("ResponseSuccess="+result)
assert result =="30";

